import { Card, CardContent } from "@/components/ui/card";
import { useTranslation } from "react-i18next";
import type { ShiftSales } from "@shared/schema";
import { format } from "date-fns";

interface ShiftCardProps {
  shift: ShiftSales;
  staffName?: string;
}

export function ShiftCard({ shift, staffName }: ShiftCardProps) {
  const { t } = useTranslation();

  const totalSales = parseFloat(shift.totalSales || "0");
  const cashSales = parseFloat(shift.cashSales || "0");
  const creditSales = parseFloat(shift.creditSales || "0");
  const upiSales = parseFloat(shift.upiSales || "0");
  const cardSales = parseFloat(shift.cardSales || "0");

  const formatCurrency = (amount: number) => {
    return `â‚¹${amount.toLocaleString()}`;
  };

  const formatShiftTime = (startTime: string | Date, endTime: string | Date) => {
    const start = new Date(startTime);
    const end = new Date(endTime);
    return `${format(start, 'h:mm a')} - ${format(end, 'h:mm a')}`;
  };

  return (
    <Card className="shadow-sm border border-gray-200" data-testid={`shift-card-${shift.id}`}>
      <CardContent className="p-4">
        <div className="flex items-center justify-between mb-3">
          <div>
            <span className="font-medium text-sm" data-testid={`shift-time-${shift.id}`}>
              {format(new Date(shift.shiftDate), 'MMM dd, yyyy')} â€¢ {formatShiftTime(shift.startTime, shift.endTime)}
            </span>
            {staffName && (
              <p className="text-xs text-gray-500" data-testid={`shift-manager-${shift.id}`}>
                {t("dataEntry.manager")}: {staffName}
              </p>
            )}
          </div>
          <span className="text-lg font-bold text-primary" data-testid={`shift-total-${shift.id}`}>
            {formatCurrency(totalSales)}
          </span>
        </div>

        <div className="grid grid-cols-2 gap-3 text-sm">
          <div className="flex justify-between">
            <span className="text-gray-600">{t("common.cash")}</span>
            <span data-testid={`shift-cash-${shift.id}`}>{formatCurrency(cashSales)}</span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-600">{t("common.credit")}</span>
            <span data-testid={`shift-credit-${shift.id}`}>{formatCurrency(creditSales)}</span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-600">{t("common.upi")}</span>
            <span data-testid={`shift-upi-${shift.id}`}>{formatCurrency(upiSales)}</span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-600">{t("common.card")}</span>
            <span data-testid={`shift-card-${shift.id}`}>{formatCurrency(cardSales)}</span>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/ui/side-menu.tsx.content:
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetHeader, SheetTitle, SheetTrigger } from "@/components/ui/sheet";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  Menu, 
  X, 
  Fuel, 
  Package,
  Truck, 
  Gauge as GaugeIcon, 
  Users, 
  Languages, 
  Settings,
  User,
  LogOut
} from "lucide-react";
import { useTranslation } from "react-i18next";
import { useAuth } from "@/hooks/useAuth";
import { useState } from "react";

interface SideMenuProps {
  onMenuItemClick?: (item: string) => void;
  currentUser?: any;
  userRole?: "dealer" | "manager";
}

export function SideMenu({ onMenuItemClick, currentUser, userRole = "dealer" }: SideMenuProps) {
  const { t, i18n } = useTranslation();
  const { user } = useAuth();
  const [open, setOpen] = useState(false);

  const displayUser = currentUser || user;

  const handleLanguageChange = (language: string) => {
    i18n.changeLanguage(language);
    localStorage.setItem('language', language);
  };

  // Define menu items based on user role
  const dealerMenuItems = [
    {
      id: "roDetails",
      label: t("menu.roDetails"),
      icon: Fuel,
    },
    {
      id: "products",
      label: "Products",
      icon: Package,
    },
    {
      id: "tankManagement",
      label: t("menu.tankManagement"),
      icon: Truck,
    },
    {
      id: "dispensingUnits",
      label: t("menu.dispensingUnits"),
      icon: GaugeIcon,
    },
    {
      id: "settings",
      label: t("menu.settings"),
      icon: Settings,
    },
  ];

  const managerMenuItems = [
    {
      id: "dataEntry",
      label: "Data Entry",
      icon: Package,
    },
    {
      id: "reports",
      label: "Reports",
      icon: Users,
    },
  ];

  const menuItems = userRole === "manager" ? managerMenuItems : dealerMenuItems;

  const handleMenuItemClick = (itemId: string) => {
    onMenuItemClick?.(itemId);
    setOpen(false);
  };

  const handleLogout = async () => {
    if (userRole === "manager") {
      try {
        // Manager logout - clear session via API
        await fetch("/api/manager/logout", {
          method: "POST",
          headers: { "Content-Type": "application/json" }
        });
      } catch (error) {
        console.error("Logout error:", error);
      }
      window.location.href = "/login";
    } else {
      // Dealer logout via Replit Auth
      window.location.href = "/api/logout";
    }
  };

  return (
    <Sheet open={open} onOpenChange={setOpen}>
      <SheetTrigger asChild>
        <Button 
          variant="outline" 
          size="icon" 
          className="bg-primary text-white border-primary hover:bg-primary/90 hover:text-white shadow-lg" 
          data-testid="menu-trigger"
        >
          <Menu className="h-5 w-5" />
        </Button>
      </SheetTrigger>
      <SheetContent side="left" className="w-80 p-0">
        <div className="bg-primary text-white p-4">
          <SheetHeader>
