import { ArrowLeft, Save } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useShiftContext } from "@/contexts/ShiftContext";

interface ShiftPageProps {
  onBack?: () => void;
}

interface ProductRate {
  productId: string;
  productName: string;
  rate: number;
  lastUpdated?: string;
  observedDensity?: number;
  observedTemperature?: number;
  densityAt15C?: number;
}

interface Shift {
  id: string;
  type: 'morning' | 'evening' | 'night';
  startTime?: string;
  endTime?: string;
  status: 'not-started' | 'active' | 'completed';
  productRates: ProductRate[];
}

export default function ShiftPage({ onBack }: ShiftPageProps) {
  const { selectedShiftType, selectedDate, setSelectedShiftType, setSelectedDate } = useShiftContext();
  const [productRates, setProductRates] = useState<ProductRate[]>([]);
  const [currentShift, setCurrentShift] = useState<Shift | null>(null);
  const { toast } = useToast();

  // Fetch products for rate entry
  const { data: products } = useQuery({
    queryKey: ['/api/manager/products'],
    retry: false,
  });

  // Fetch current shift data
  const { data: shiftData } = useQuery({
    queryKey: ['/api/shifts/current'],
    retry: false,
  });

  // Fetch last saved rates
  const { data: lastRates } = useQuery({
    queryKey: ['/api/shifts/last-rates', selectedDate, selectedShiftType],
    queryFn: () => {
      const params = new URLSearchParams({
        date: selectedDate,
        shiftType: selectedShiftType
      });
      return fetch(`/api/shifts/last-rates?${params}`).then(res => res.json());
    },
    retry: false,
  });

  // Initialize product rates when products load
  useEffect(() => {
    if (products && products.length > 0) {
      const initialRates = products.map((product: any) => {
        const lastRate = lastRates?.find((rate: any) => rate.productId === product.id);
        return {
          productId: product.id,
          productName: product.name,
          rate: lastRate?.rate || 0,
          lastUpdated: lastRate?.lastUpdated,
        };
      });
      setProductRates(initialRates);
    }
  }, [products, lastRates]);

  // Save rates mutation
  const saveRatesMutation = useMutation({
    mutationFn: async (rates: ProductRate[]) => {
      return apiRequest('/api/shifts/rates', 'POST', {
        shiftType: selectedShiftType,
        date: selectedDate,
        rates: rates,
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Product rates saved successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/shifts/last-rates', selectedDate, selectedShiftType] });
    },
    onError: (error) => {
      console.error('Save rates error:', error);
      toast({
        title: "Error",
        description: "Failed to save product rates",
        variant: "destructive",
      });
    },
  });



  const handleRateChange = (productId: string, rate: string) => {
    const numericRate = parseFloat(rate) || 0;
    setProductRates(prev => 
      prev.map(item => 
        item.productId === productId 
          ? { ...item, rate: numericRate }
          : item
      )
    );
  };

  const handleDensityChange = (productId: string, density: string) => {
    const numericDensity = parseFloat(density) || 0;
    setProductRates(prev => 
      prev.map(item => {
        if (item.productId === productId) {
          const updatedItem = { ...item, observedDensity: numericDensity };
          // Auto-calculate density at 15Â°C if both density and temperature are available
          if (updatedItem.observedTemperature && numericDensity) {
            updatedItem.densityAt15C = calculateDensityAt15C(numericDensity, updatedItem.observedTemperature);
          }
          return updatedItem;
        }
        return item;
      })
    );
  };

  const handleTemperatureChange = (productId: string, temperature: string) => {
    const numericTemp = parseFloat(temperature) || 0;
    setProductRates(prev => 
      prev.map(item => {
        if (item.productId === productId) {
          const updatedItem = { ...item, observedTemperature: numericTemp };
          // Auto-calculate density at 15Â°C if both density and temperature are available
          if (updatedItem.observedDensity && numericTemp) {
            updatedItem.densityAt15C = calculateDensityAt15C(updatedItem.observedDensity, numericTemp);
          }
          return updatedItem;
        }
        return item;
      })
    );
  };

  // Formula to calculate density at 15Â°C from observed density and temperature
  const calculateDensityAt15C = (observedDensity: number, observedTemp: number): number => {
    // Using the standard petroleum density correction formula
    // Density at 15Â°C = Observed Density * [1 + 0.0008 * (Observed Temp - 15)]
    const correctionFactor = 1 + 0.0008 * (observedTemp - 15);
    return Math.round((observedDensity * correctionFactor) * 100) / 100; // Round to 2 decimal places for Kg/mÂ³
  };

  const handleSaveRates = () => {
    saveRatesMutation.mutate(productRates);
  };



  return (
    <div className="min-h-screen bg-surface pb-20">
      <div className="bg-primary text-white p-4">
        <div className="flex items-center space-x-3">
          {onBack && (
            <Button
              variant="ghost"
              size="icon"
              onClick={onBack}
              className="text-white hover:bg-white/20"
              data-testid="back-button"
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
          )}
          <h1 className="text-xl font-semibold" data-testid="page-title">
            Shift Management
          </h1>
        </div>
      </div>

      <div className="p-4 space-y-6">
        {/* Shift Selection */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          <h2 className="text-lg font-semibold mb-4">Select Shift</h2>
          <div className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="shift-date">Date</Label>
                <Input
                  id="shift-date"
                  type="date"
                  value={selectedDate}
                  onChange={(e) => setSelectedDate(e.target.value)}
                  className="mt-1"
                  data-testid="shift-date-select"
                />
              </div>

              <div>
                <Label htmlFor="shift-type">Shift Type</Label>
                <Select 
                  value={selectedShiftType} 
                  onValueChange={(value: 'morning' | 'evening' | 'night') => setSelectedShiftType(value)}
                >
                  <SelectTrigger className="mt-1" data-testid="shift-type-select">
                    <SelectValue placeholder="Select shift type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="morning">Morning Shift (6 AM - 2 PM)</SelectItem>
                    <SelectItem value="evening">Evening Shift (2 PM - 10 PM)</SelectItem>
                    <SelectItem value="night">Night Shift (10 PM - 6 AM)</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>


          </div>
        </div>

        {/* Product Rates Section */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold">Product Rates</h2>
            <Button 
              onClick={handleSaveRates}
              disabled={saveRatesMutation.isPending}
              size="sm"
              data-testid="save-rates"
            >
              <Save className="h-4 w-4 mr-2" />
              Save Rates
            </Button>
          </div>

          <div className="space-y-4">
            {productRates.length > 0 ? (
              productRates.map((product) => (
                <div key={product.productId} className="flex items-center justify-between p-4 border rounded-lg">
                  <div className="flex-1">
                    <h3 className="font-medium">{product.productName}</h3>
                    {product.lastUpdated && (
                      <p className="text-sm text-gray-500">
                        Last updated: {new Date(product.lastUpdated).toLocaleDateString()}
                      </p>
                    )}
                  </div>
                  <div className="flex items-center space-x-2">
                    <Label htmlFor={`rate-${product.productId}`} className="text-sm">â‚¹</Label>
                    <Input
                      id={`rate-${product.productId}`}
                      type="number"
                      step="0.01"
                      value={product.rate}
                      onChange={(e) => handleRateChange(product.productId, e.target.value)}
                      className="w-24"
                      data-testid={`rate-input-${product.productId}`}
                    />
                    <span className="text-sm text-gray-500">/L</span>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center text-gray-500 py-8" data-testid="no-products">
                No products configured. Please set up products first.
              </div>
            )}
          </div>
        </div>

        {/* Density Measurements Section */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          <h2 className="text-lg font-semibold mb-4">Density Measurements</h2>

          <div className="space-y-4">
            {productRates.length > 0 ? (
              productRates.map((product) => (
                <div key={`density-${product.productId}`} className="p-4 border rounded-lg">
                  <h3 className="font-medium mb-4">{product.productName}</h3>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                      <Label htmlFor={`density-${product.productId}`} className="text-sm text-gray-600">
                        Observed Density (Kg/mÂ³)
                      </Label>
                      <Input
                        id={`density-${product.productId}`}
                        type="number"
                        step="0.01"
                        placeholder="750.00"
                        value={product.observedDensity || ''}
                        onChange={(e) => handleDensityChange(product.productId, e.target.value)}
                        className="w-full mt-1"
                        data-testid={`density-input-${product.productId}`}
                      />
                    </div>

                    <div>
                      <Label htmlFor={`temperature-${product.productId}`} className="text-sm text-gray-600">
                        Temperature (Â°C)
                      </Label>
                      <Input
                        id={`temperature-${product.productId}`}
                        type="number"
                        step="0.1"
                        placeholder="25.0"
                        value={product.observedTemperature || ''}
                        onChange={(e) => handleTemperatureChange(product.productId, e.target.value)}
                        className="w-full mt-1"
                        data-testid={`temperature-input-${product.productId}`}
                      />
                    </div>

                    <div>
                      <Label className="text-sm text-gray-600">Density at 15Â°C</Label>
                      <div className="mt-1 p-3 bg-gray-50 rounded border text-sm font-medium">
                        {product.densityAt15C ? `${product.densityAt15C} Kg/mÂ³` : '---'}
                      </div>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center text-gray-500 py-8">
                No products available for density measurements.
              </div>
            )}
          </div>
        </div>


      </div>
    </div>
  );
}

client/src/pages/stock.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { ArrowLeft, Package, Save, Edit3 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import ShiftDateDisplay from "@/components/ShiftDateDisplay";
import { useShiftContext } from "@/contexts/ShiftContext";

interface StockPageProps {
  onBack?: () => void;
}

interface Tank {
  id: string;
  tankNumber: string;
  capacity: string;
  productName: string;
  productId: string;
  isActive: boolean;
}

interface StockEntry {
  id: string;
  tankId: string;
  shiftType: string;
  shiftDate: string;
  openingStock: string;
  receipt: string;
  invoiceValue: string;
  tankNumber: string;
  productName: string;
  productId: string;
}

export default function StockPage({ onBack }: StockPageProps) {
  const { selectedShiftType, selectedDate } = useShiftContext();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [editingEntries, setEditingEntries] = useState<Record<string, boolean>>({});
  const [formData, setFormData] = useState<Record<string, {
    openingStock: string;
    receipt: string;
    invoiceValue: string;
  }>>({});

  // Fetch tanks data
  const { data: tanks = [], isLoading: tanksLoading } = useQuery({
    queryKey: ["/api/manager/tanks"],
    enabled: true,
  });

  // Fetch existing stock entries for selected shift and date
  const { data: stockEntries = [], isLoading: stockLoading } = useQuery({
    queryKey: ["/api/manager/stock", selectedShiftType, selectedDate],
    enabled: !!selectedShiftType && !!selectedDate,
  });

  // Create stock entry mutation
  const createStockEntry = useMutation({
    mutationFn: async (data: any) => {
      const response = await fetch("/api/manager/stock", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to create stock entry");
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Success", description: "Stock entry saved successfully" });
      queryClient.invalidateQueries({ queryKey: ["/api/manager/stock"] });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to save stock entry", variant: "destructive" });
    },
  });

  // Update stock entry mutation
  const updateStockEntry = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await fetch(`/api/manager/stock/${id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to update stock entry");
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Success", description: "Stock entry updated successfully" });
      queryClient.invalidateQueries({ queryKey: ["/api/manager/stock"] });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to update stock entry", variant: "destructive" });
    },
  });

  // Initialize form data when tanks or stock entries change
  useEffect(() => {
    if (tanks.length > 0) {
      const initialData: Record<string, any> = {};

      tanks.forEach((tank: Tank) => {
        const existingEntry = stockEntries.find((entry: StockEntry) => entry.tankId === tank.id);
        initialData[tank.id] = {
          openingStock: existingEntry?.openingStock || "",
          receipt: existingEntry?.receipt || "",
          invoiceValue: existingEntry?.invoiceValue || "",
        };
      });

      setFormData(prev => {
        // Only update if the data has actually changed to prevent infinite loops
        const hasChanged = JSON.stringify(prev) !== JSON.stringify(initialData);
        return hasChanged ? initialData : prev;
      });
    }
  }, [tanks.length, stockEntries.length]);

  const handleInputChange = (tankId: string, field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [tankId]: {
        ...prev[tankId],
        [field]: value,
      },
    }));
  };

  const handleSave = async (tankId: string) => {
    const data = formData[tankId];
    if (!data || !selectedShiftType || !selectedDate) return;

    const existingEntry = stockEntries.find((entry: StockEntry) => entry.tankId === tankId);

    const stockData = {
      tankId,
      shiftType: selectedShiftType,
      shiftDate: selectedDate,
      openingStock: data.openingStock,
      receipt: data.receipt,
      invoiceValue: data.invoiceValue,
    };

    if (existingEntry) {
      await updateStockEntry.mutateAsync({ id: existingEntry.id, data: stockData });
    } else {
      await createStockEntry.mutateAsync(stockData);
    }

    setEditingEntries(prev => ({ ...prev, [tankId]: false }));
  };

  const toggleEdit = (tankId: string) => {
    setEditingEntries(prev => ({ ...prev, [tankId]: !prev[tankId] }));
  };

  if (tanksLoading || stockLoading) {
    return (
      <div className="min-h-screen bg-surface pb-20">
        <div className="bg-primary text-white p-4">
          <div className="flex items-center space-x-3">
            {onBack && (
              <Button
                variant="ghost"
                size="icon"
                onClick={onBack}
                className="text-white hover:bg-white/20"
                data-testid="back-button"
              >
                <ArrowLeft className="h-5 w-5" />
              </Button>
            )}
            <h1 className="text-xl font-semibold" data-testid="page-title">
              Stock Management
            </h1>
          </div>
        </div>
        <div className="p-4">
          <div className="text-center py-8">Loading...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-surface pb-20">
      <div className="bg-primary text-white p-4">
        <div className="flex items-center space-x-3">
          {onBack && (
            <Button
              variant="ghost"
              size="icon"
              onClick={onBack}
              className="text-white hover:bg-white/20"
              data-testid="back-button"
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
          )}
          <h1 className="text-xl font-semibold" data-testid="page-title">
            Stock Management
          </h1>
        </div>
      </div>

      <div className="p-4">
        <ShiftDateDisplay
          selectedShiftType={selectedShiftType}
          selectedDate={selectedDate}
        />

        <div className="grid grid-cols-2 gap-4 mb-6">
          <div className="bg-white rounded-lg shadow-sm p-4">
            <div className="flex items-center space-x-3">
              <Package className="h-8 w-8 text-primary" />
              <div>
                <h3 className="text-sm font-medium text-gray-600">Total Tanks</h3>
                <p className="text-xl font-semibold" data-testid="total-tanks">{tanks.length}</p>
              </div>
            </div>
          </div>
          <div className="bg-white rounded-lg shadow-sm p-4">
            <div className="flex items-center space-x-3">
              <Package className="h-8 w-8 text-green-500" />
              <div>
                <h3 className="text-sm font-medium text-gray-600">Entries Saved</h3>
                <p className="text-xl font-semibold" data-testid="entries-saved">{stockEntries.length}</p>
              </div>
            </div>
          </div>
        </div>

        <div className="space-y-4">
          <h2 className="text-lg font-semibold">Tank Stock Information</h2>

          {tanks.map((tank: Tank) => {
            const existingEntry = stockEntries.find((entry: StockEntry) => entry.tankId === tank.id);
            const isEditing = editingEntries[tank.id];
            const tankFormData = formData[tank.id] || { openingStock: "", receipt: "", invoiceValue: "" };

            return (
              <Card key={tank.id} className="bg-white">
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-lg">
                      Tank {tank.tankNumber} - {tank.productName}
                    </CardTitle>
                    <div className="flex items-center space-x-2">
                      {existingEntry && (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => toggleEdit(tank.id)}
                          data-testid={`edit-tank-${tank.id}`}
                        >
                          <Edit3 className="h-4 w-4 mr-1" />
                          {isEditing ? "Cancel" : "Edit"}
                        </Button>
                      )}
                      <Button
                        onClick={() => handleSave(tank.id)}
                        disabled={createStockEntry.isPending || updateStockEntry.isPending}
                        data-testid={`save-tank-${tank.id}`}
                      >
                        <Save className="h-4 w-4 mr-1" />
                        Save
                      </Button>
                    </div>
                  </div>
                  <div className="text-sm text-gray-500">
                    Capacity: {tank.capacity}L
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="grid grid-cols-1 gap-4">
                    <div>
                      <Label htmlFor={`opening-${tank.id}`}>Opening Stock (Liters)</Label>
                      <Input
                        id={`opening-${tank.id}`}
                        type="number"
                        placeholder="Enter opening stock in liters"
                        value={tankFormData.openingStock}
                        onChange={(e) => handleInputChange(tank.id, "openingStock", e.target.value)}
                        disabled={existingEntry && !isEditing}
                        data-testid={`input-opening-stock-${tank.id}`}
                      />
                    </div>
                    <div>
                      <Label htmlFor={`receipt-${tank.id}`}>Receipt (Liters)</Label>
                      <Input
                        id={`receipt-${tank.id}`}
                        type="number"
                        placeholder="Enter liters received"
                        value={tankFormData.receipt}
                        onChange={(e) => handleInputChange(tank.id, "receipt", e.target.value)}
                        disabled={existingEntry && !isEditing}
                        data-testid={`input-receipt-${tank.id}`}
                      />
                    </div>
                    <div>
                      <Label htmlFor={`invoice-${tank.id}`}>Invoice Value (â‚¹)</Label>
                      <Input
                        id={`invoice-${tank.id}`}
                        type="number"
                        placeholder="Enter total invoice amount"
                        value={tankFormData.invoiceValue}
                        onChange={(e) => handleInputChange(tank.id, "invoiceValue", e.target.value)}
                        disabled={existingEntry && !isEditing}
                        data-testid={`input-invoice-value-${tank.id}`}
                      />
                    </div>
                  </div>

                  {existingEntry && (
                    <div className="text-xs text-gray-500 pt-2 border-t">
                      Last updated: {new Date(existingEntry.createdAt).toLocaleString()}
                    </div>
                  )}
                </CardContent>
              </Card>
            );
          })}

          {tanks.length === 0 && (
            <div className="text-center text-gray-500 py-8" data-testid="no-tanks">
              No tanks available. Please add tanks in Tank Management first.
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/pages/manager-login.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Eye, EyeOff, AlertCircle, Building2, User } from "lucide-react";
import { useTranslation } from "react-i18next";
import { apiRequest } from "@/lib/queryClient";

const loginSchema = z.object({
  role: z.enum(["dealer", "manager"]),
  phoneNumber: z.string().min(1, "Phone number is required"),
  password: z.string().optional(),
}).refine((data) => {
  // Password is required for managers
  if (data.role === "manager") {
    return data.password && data.password.length > 0;
  }
  return true;
}, {
  message: "Password is required for managers",
  path: ["password"],
});

type LoginForm = z.infer<typeof loginSchema>;

export default function UnifiedLogin() {
  const { t } = useTranslation();
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const form = useForm<LoginForm>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      role: "dealer",
      phoneNumber: "",
      password: "",
    },
  });

  const selectedRole = form.watch("role");

  const onSubmit = async (data: LoginForm) => {
    setIsLoading(true);
    setError(null);

    try {
      if (data.role === "dealer") {
        // Redirect to Replit Auth for dealer login
        window.location.href = "/api/login";
      } else {
        // Manager login via API
        const response = await apiRequest("/api/manager/login", "POST", {
          phoneNumber: data.phoneNumber,
          password: data.password,
        });

        console.log("Manager login response:", response);

        if (response.success) {
          // Redirect to manager dashboard
          window.location.reload();
        } else {
          setError(response.message || "Invalid manager credentials");
        }
      }
    } catch (err: any) {
      setError(err.message || "Login failed. Please check your credentials.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <CardTitle className="text-2xl font-bold text-gray-900">
            FuelFlow Login
          </CardTitle>
          <CardDescription>
            Sign in to access your dashboard
          </CardDescription>
        </CardHeader>
        <CardContent>
          {error && (
            <Alert variant="destructive" className="mb-4">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="role"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Login as</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-role">
                          <SelectValue placeholder="Select your role" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="dealer">
                          <div className="flex items-center space-x-2">
                            <Building2 className="h-4 w-4" />
                            <span>Dealer/Owner</span>
                          </div>
                        </SelectItem>
                        <SelectItem value="manager">
                          <div className="flex items-center space-x-2">
                            <User className="h-4 w-4" />
                            <span>Manager</span>
                          </div>
                        </SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="phoneNumber"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      {selectedRole === "dealer" ? "Phone Number" : "Phone Number (Login ID)"}
                    </FormLabel>
                    <FormControl>
                      <Input 
                        placeholder={selectedRole === "dealer" 
                          ? "Enter your phone number" 
                          : "Enter manager phone number"
                        }
                        {...field} 
                        data-testid="input-phone-number"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {selectedRole === "manager" && (
                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Input 
                            type={showPassword ? "text" : "password"}
                            placeholder="Enter your password" 
                            {...field} 
                            data-testid="input-password"
                          />
                          <Button
                            type="button"
                            variant="ghost"
                            size="icon"
                            className="absolute right-2 top-1/2 transform -translate-y-1/2 h-8 w-8"
                            onClick={() => setShowPassword(!showPassword)}
                            data-testid="toggle-password"
                          >
                            {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                          </Button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              )}

              <Button
                type="submit"
                disabled={isLoading}
                className="w-full bg-primary text-white"
                data-testid="button-login"
              >
                {isLoading ? "Signing in..." : `Sign in as ${selectedRole === "dealer" ? "Dealer" : "Manager"}`}
              </Button>
            </form>
          </Form>

          {selectedRole === "dealer" && (
            <div className="mt-4 p-3 bg-blue-50 rounded-lg">
              <p className="text-xs text-blue-700">
                Dealers will be redirected to secure authentication. Managers use credentials provided by the dealer.
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/ShiftDateDisplay.t.content:
interface ShiftDateDisplayProps {
  selectedShiftType: 'morning' | 'evening' | 'night';
  selectedDate: string;
}

const getShiftDisplayName = (shiftType: 'morning' | 'evening' | 'night') => {
  switch (shiftType) {
    case 'morning':
      return 'Morning Shift (6 AM - 2 PM)';
    case 'evening':
      return 'Evening Shift (2 PM - 10 PM)';
    case 'night':
      return 'Night Shift (10 PM - 6 AM)';
  }
};

export default function ShiftDateDisplay({
  selectedShiftType,
  selectedDate,
}: ShiftDateDisplayProps) {
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      weekday: 'short', 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric' 
    });
  };

  return (
    <div className="bg-blue-50 border border-blue-200 rounded-lg p-3 mb-4">
      <div className="text-xs text-blue-600 font-medium">
        Viewing data for: {getShiftDisplayName(selectedShiftType)} on {formatDate(selectedDate)}
      </div>
    </div>
  );
}

client/src/contexts/ShiftContext.tsx.content:
import { createContext, useContext, useState, ReactNode } from 'react';

type ShiftType = 'morning' | 'evening' | 'night';

interface ShiftContextType {
  selectedShiftType: ShiftType;
  selectedDate: string;
  setSelectedShiftType: (shiftType: ShiftType) => void;
  setSelectedDate: (date: string) => void;
}

const ShiftContext = createContext<ShiftContextType | undefined>(undefined);

export const useShiftContext = () => {
  const context = useContext(ShiftContext);
  if (context === undefined) {
    throw new Error('useShiftContext must be used within a ShiftProvider');
  }
  return context;
};

interface ShiftProviderProps {
  children: ReactNode;
}

export const ShiftProvider = ({ children }: ShiftProviderProps) => {
  const [selectedShiftType, setSelectedShiftType] = useState<ShiftType>('morning');
  const [selectedDate, setSelectedDate] = useState<string>(
    new Date().toISOString().split('T')[0]
  );

  return (
    <ShiftContext.Provider
      value={{
        selectedShiftType,
        selectedDate,
        setSelectedShiftType,
        setSelectedDate,
      }}
    >
      {children}
    </ShiftContext.Provider>
  );
};

client/src/hooks/useManagerAuth.ts.content:
import { useQuery } from "@tanstack/react-query";
import { getQueryFn } from "@/lib/queryClient";

export function useManagerAuth() {
  const { data: manager, isLoading } = useQuery({
    queryKey: ["/api/auth/manager"],
    queryFn: getQueryFn({ on401: "returnNull" }),
    retry: false,
    refetchInterval: false,
    refetchOnWindowFocus: false,
    refetchOnMount: false,
    refetchOnReconnect: false,
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  return {
    manager,
    isLoading,
    isManagerAuthenticated: !!manager,
  };
}

client/src/pages/dispensing-units.tsx.content:
import { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ArrowLeft, Plus, GaugeIcon, Edit3, Trash2, Fuel, Calendar } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { format } from "date-fns";
import { Calendar as CalendarComponent } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";

interface DispensingUnitsPageProps {
  onBack: () => void;
}

const nozzleSchema = z.object({
  tankId: z.string().min(1, "Tank selection is required"),
  calibrationValidUntil: z.date({
    required_error: "Calibration date is required",
  }),
});

const duSchema = z.object({
  name: z.string().min(1, "DU name is required"),
  numberOfNozzles: z.number().min(1, "At least 1 nozzle is required").max(6, "Maximum 6 nozzles allowed"),
  nozzles: z.array(nozzleSchema),
});

type DUForm = z.infer<typeof duSchema>;

export default function DispensingUnitsPage({ onBack }: DispensingUnitsPageProps) {
  const { t } = useTranslation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingDU, setEditingDU] = useState<any>(null);

  const { data: dispensingUnits = [], isLoading: duLoading } = useQuery<any[]>({
    queryKey: ["/api/dispensing-units"],
  });

  const { data: tanks = [], isLoading: tanksLoading } = useQuery<any[]>({
    queryKey: ["/api/tanks"],
  });

  const getDefaultCalibrationDate = () => {
    const date = new Date();
    date.setFullYear(date.getFullYear() + 1);
    return date;
  };

  const form = useForm<DUForm>({
    resolver: zodResolver(duSchema),
    defaultValues: {
      name: "",
      numberOfNozzles: 2,
      nozzles: [
        { tankId: "", calibrationValidUntil: getDefaultCalibrationDate() },
        { tankId: "", calibrationValidUntil: getDefaultCalibrationDate() }
      ],
    },
  });

  const numberOfNozzles = form.watch("numberOfNozzles");

  // Update nozzles array when number of nozzles changes
  useEffect(() => {
    const currentNozzles = form.getValues("nozzles");

    const newNozzles = Array.from({ length: numberOfNozzles }, (_, index) => 
      currentNozzles[index] || { tankId: "", calibrationValidUntil: getDefaultCalibrationDate() }
    );
    form.setValue("nozzles", newNozzles);
  }, [numberOfNozzles, form]);

  const createMutation = useMutation({
    mutationFn: async (data: DUForm) => {
      return apiRequest("/api/dispensing-units", "POST", data);
    },
    onSuccess: () => {
      toast({
        title: t("common.success"),
        description: t("dispensingUnits.createSuccess"),
      });
      setIsDialogOpen(false);
      form.reset();
      queryClient.invalidateQueries({ queryKey: ["/api/dispensing-units"] });
    },
    onError: (error) => {
      toast({
        title: t("common.error"),
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: DUForm }) => {
      return apiRequest(`/api/dispensing-units/${id}`, "PATCH", data);
    },
    onSuccess: () => {
      toast({
        title: t("common.success"),
        description: t("dispensingUnits.updateSuccess"),
      });
      setIsDialogOpen(false);
      setEditingDU(null);
      form.reset();
      queryClient.invalidateQueries({ queryKey: ["/api/dispensing-units"] });
    },
    onError: (error) => {
      toast({
        title: t("common.error"),
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest(`/api/dispensing-units/${id}`, "DELETE");
    },
    onSuccess: () => {
      toast({
        title: t("common.success"),
        description: t("dispensingUnits.deleteSuccess"),
      });
      queryClient.invalidateQueries({ queryKey: ["/api/dispensing-units"] });
    },
    onError: (error) => {
      toast({
        title: t("common.error"),
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (data: DUForm) => {
    console.log("Form submitted with data:", data);
    console.log("Available tanks:", tanks);

    if (editingDU) {
      updateMutation.mutate({ id: editingDU.id, data });
    } else {
      createMutation.mutate(data);
    }
  };

  const handleEdit = (du: any) => {
    setEditingDU(du);
    form.reset({
      name: du.name,
      numberOfNozzles: du.numberOfNozzles,
      nozzles: du.nozzles || [],
    });
    setIsDialogOpen(true);
  };

  const handleAddNew = () => {
    setEditingDU(null);
    form.reset({
      name: "",
      numberOfNozzles: 2,
      nozzles: [
        { tankId: "", calibrationValidUntil: getDefaultCalibrationDate() },
        { tankId: "", calibrationValidUntil: getDefaultCalibrationDate() }
      ],
    });
    setIsDialogOpen(true);
  };

  const getTankName = (tankId: string) => {
    const tank = tanks.find((t: any) => t.id === tankId);
    return tank ? tank.tankNumber : "Unknown Tank";
  };

  const getFuelTypeColor = () => {
    return "text-blue-600 bg-blue-50";
  };

  if (duLoading || tanksLoading) {
    return (
      <div className="p-4">
        <div className="animate-pulse space-y-4">
          <div className="h-4 bg-gray-200 rounded w-3/4"></div>
          <div className="h-20 bg-gray-200 rounded"></div>
          <div className="h-20 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4 pb-20">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center">
          <Button
            variant="ghost"
            size="icon"
            onClick={onBack}
            className="mr-3"
            data-testid="back-button"
          >
            <ArrowLeft className="h-5 w-5" />
          </Button>
          <h1 className="text-xl font-semibold" data-testid="page-title">
            {t("menu.dispensingUnits")}
          </h1>
        </div>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={handleAddNew} data-testid="add-du-button">
              <Plus className="h-4 w-4 mr-2" />
              {t("dispensingUnits.addDU")}
            </Button>
          </DialogTrigger>
          <DialogContent className="max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>
                {editingDU ? t("dispensingUnits.editDU") : t("dispensingUnits.addDU")}
              </DialogTitle>
              <DialogDescription>
                {editingDU ? t("dispensingUnits.editDescription") : t("dispensingUnits.addDescription")}
              </DialogDescription>
            </DialogHeader>
            <div className="max-h-[60vh] overflow-y-auto pr-2">
              <form id="du-form" onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="name">{t("dispensingUnits.duName")}</Label>
                <Input
                  id="name"
                  {...form.register("name")}
                  placeholder="e.g., DU-01, Pump A"
                  data-testid="input-du-name"
                />
                {form.formState.errors.name && (
                  <p className="text-sm text-red-600">{form.formState.errors.name.message}</p>
                )}
              </div>

              <div className="space-y-2">
                <Label htmlFor="numberOfNozzles">{t("dispensingUnits.numberOfNozzles")}</Label>
                <Select
                  value={form.watch("numberOfNozzles").toString()}
                  onValueChange={(value) => form.setValue("numberOfNozzles", parseInt(value))}
                >
                  <SelectTrigger data-testid="select-nozzles">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="1">1</SelectItem>
                    <SelectItem value="2">2</SelectItem>
                    <SelectItem value="4">4</SelectItem>
                    <SelectItem value="6">6</SelectItem>
                  </SelectContent>
                </Select>
                {form.formState.errors.numberOfNozzles && (
                  <p className="text-sm text-red-600">{form.formState.errors.numberOfNozzles.message}</p>
                )}
              </div>

              {/* Dynamic Nozzle Configuration */}
              <div className="space-y-4">
                <Label className="text-base font-medium">Nozzle Configuration</Label>
                {Array.from({ length: numberOfNozzles }, (_, index) => (
                  <div key={index} className="border rounded-lg p-4 space-y-3">
                    <h4 className="font-medium">Nozzle #{index + 1}</h4>

                    <div className="space-y-2">
                      <Label>Connected Tank</Label>
                      <Select
                        value={form.watch(`nozzles.${index}.tankId`) || ""}
                        onValueChange={(value) => form.setValue(`nozzles.${index}.tankId`, value)}
                      >
                        <SelectTrigger data-testid={`select-nozzle-${index}-tank`}>
                          <SelectValue placeholder="Select tank" />
                        </SelectTrigger>
                        <SelectContent>
                          {tanks.length > 0 ? tanks.map((tank: any) => (
                            <SelectItem key={tank.id} value={tank.id}>
                              {tank.tankNumber}
                            </SelectItem>
                          )) : (
                            <SelectItem value="" disabled>No tanks available</SelectItem>
                          )}
                        </SelectContent>
                      </Select>
                    </div>

                    <div className="space-y-2">
                      <Label>Calibration Valid Until</Label>
                      <Popover>
                        <PopoverTrigger asChild>
                          <Button
                            variant="outline"
                            className="w-full justify-start text-left font-normal"
                            data-testid={`select-nozzle-${index}-calibration`}
                          >
                            <Calendar className="mr-2 h-4 w-4" />
                            {form.watch(`nozzles.${index}.calibrationValidUntil`) 
                              ? format(form.watch(`nozzles.${index}.calibrationValidUntil`), "PPP")
                              : "Pick a date"
                            }
                          </Button>
                        </PopoverTrigger>
                        <PopoverContent className="w-auto p-0">
                          <CalendarComponent
                            mode="single"
                            selected={form.watch(`nozzles.${index}.calibrationValidUntil`)}
                            onSelect={(date) => form.setValue(`nozzles.${index}.calibrationValidUntil`, date || new Date())}
                            initialFocus
                          />
                        </PopoverContent>
                      </Popover>
                    </div>
                  </div>
                ))}
              </div>

              </form>
            </div>
            <div className="flex space-x-3 pt-4 border-t bg-white sticky bottom-0">
              <Button
                type="submit"
                form="du-form"
                disabled={createMutation.isPending || updateMutation.isPending}
                className="flex-1"
                data-testid="save-du-button"
              >
                {(createMutation.isPending || updateMutation.isPending) ? 
                  t("common.saving") : t("common.save")}
              </Button>
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsDialogOpen(false)}
                className="flex-1"
                data-testid="cancel-du-button"
              >
                {t("common.cancel")}
              </Button>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Notice about tank requirement */}
      {tanks.length === 0 && (
        <Card className="mb-4 border-orange-200 bg-orange-50">
          <CardContent className="p-4">
            <div className="flex items-center space-x-2 text-orange-800">
              <Fuel className="h-5 w-5" />
              <p className="text-sm">
                {t("dispensingUnits.noTanksNotice")}
              </p>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Dispensing Units List */}
      {dispensingUnits.length === 0 ? (
        <Card className="text-center py-8">
          <CardContent>
            <GaugeIcon className="h-12 w-12 mx-auto text-gray-400 mb-4" />
            <h3 className="text-lg font-medium mb-2">{t("dispensingUnits.noDUs")}</h3>
            <p className="text-gray-600 mb-4">{t("dispensingUnits.noDUsDescription")}</p>
            <Button 
              onClick={handleAddNew} 
              disabled={tanks.length === 0}
              data-testid="add-first-du-button"
            >
              <Plus className="h-4 w-4 mr-2" />
              {t("dispensingUnits.addFirstDU")}
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-4">
          {dispensingUnits.map((du: any) => (
            <Card key={du.id} data-testid={`du-card-${du.id}`}>
              <CardContent className="p-4">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-3">
                    <div className="p-2 rounded-lg bg-primary/10">
                      <GaugeIcon className="h-5 w-5 text-primary" />
                    </div>
                    <div>
                      <h3 className="font-medium" data-testid={`du-name-${du.id}`}>
                        {du.name}
                      </h3>
                      <div className="flex flex-col space-y-1">
                        <span className={`inline-block px-2 py-1 rounded-full text-xs font-medium w-fit ${getFuelTypeColor()}`}>
                          DU Unit
                        </span>
                        <span className="text-xs text-gray-600">
                          {du.numberOfNozzles} nozzles
                        </span>
                      </div>
                    </div>
                  </div>
                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => handleEdit(du)}
                      data-testid={`edit-du-${du.id}`}
                    >
                      <Edit3 className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => deleteMutation.mutate(du.id)}
                      disabled={deleteMutation.isPending}
                      data-testid={`delete-du-${du.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>

                {/* Nozzle indicators */}
                <div className="flex flex-wrap gap-2">
                  {Array.from({ length: du.nozzles }, (_, i) => (
                    <div
                      key={i}
                      className="flex items-center space-x-1 bg-gray-100 px-2 py-1 rounded text-xs"
                    >
                      <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                      <span>Nozzle {i + 1}</span>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/pages/login.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Eye, EyeOff, AlertCircle, Building2, User } from "lucide-react";
import { useTranslation } from "react-i18next";
import { apiRequest } from "@/lib/queryClient";

const loginSchema = z.object({
  role: z.enum(["dealer", "manager"]),
  phoneNumber: z.string().min(1, "Phone number is required"),
  password: z.string().optional(),
}).refine((data) => {
  // Password is required for managers
  if (data.role === "manager") {
    return data.password && data.password.length > 0;
  }
  return true;
}, {
  message: "Password is required for managers",
  path: ["password"],
});

type LoginForm = z.infer<typeof loginSchema>;

export default function Login() {
  const { t } = useTranslation();
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  const form = useForm<LoginForm>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      role: "dealer",
      phoneNumber: "",
      password: "",
    },
  });

  const selectedRole = form.watch("role");

  const onSubmit = async (data: LoginForm) => {
    setIsLoading(true);
    setError(null);

    try {
      if (data.role === "dealer") {
        // Redirect to Replit Auth for dealer login
        window.location.href = "/api/login";
      } else {
        // Manager login via API
        const response = await apiRequest("/api/manager/login", "POST", {
          phoneNumber: data.phoneNumber,
          password: data.password,
        });

        if (response.success) {
          // Redirect to manager dashboard
          window.location.reload();
        } else {
          setError(response.message || "Invalid manager credentials");
        }
      }
    } catch (err: any) {
      setError(err.message || "Login failed. Please check your credentials.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <CardTitle className="text-2xl font-bold text-gray-900">
            FuelFlow Login
          </CardTitle>
          <CardDescription>
            Sign in to access your dashboard
          </CardDescription>
        </CardHeader>
        <CardContent>
          {error && (
            <Alert variant="destructive" className="mb-4">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="role"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Login as</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-role">
                          <SelectValue placeholder="Select your role" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="dealer">
                          <div className="flex items-center space-x-2">
                            <Building2 className="h-4 w-4" />
                            <span>Dealer/Owner</span>
                          </div>
                        </SelectItem>
                        <SelectItem value="manager">
                          <div className="flex items-center space-x-2">
                            <User className="h-4 w-4" />
                            <span>Manager</span>
                          </div>
                        </SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="phoneNumber"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>
                      {selectedRole === "dealer" ? "Phone Number" : "Phone Number (Login ID)"}
                    </FormLabel>
                    <FormControl>
                      <Input 
                        placeholder={selectedRole === "dealer" 
                          ? "Enter your phone number" 
                          : "Enter manager phone number"
                        }
                        {...field} 
                        data-testid="input-phone-number"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {selectedRole === "manager" && (
                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Input 
                            type={showPassword ? "text" : "password"}
                            placeholder="Enter your password" 
                            {...field} 
                            data-testid="input-password"
                          />
                          <Button
                            type="button"
                            variant="ghost"
                            size="icon"
                            className="absolute right-2 top-1/2 transform -translate-y-1/2 h-8 w-8"
                            onClick={() => setShowPassword(!showPassword)}
                            data-testid="toggle-password"
                          >
                            {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                          </Button>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              )}

              <Button
                type="submit"
                disabled={isLoading}
                className="w-full bg-primary text-white"
                data-testid="button-login"
              >
                {isLoading ? "Signing in..." : `Sign in as ${selectedRole === "dealer" ? "Dealer" : "Manager"}`}
              </Button>
            </form>
          </Form>

          {selectedRole === "dealer" && (
            <div className="mt-4 p-3 bg-blue-50 rounded-lg">
              <p className="text-xs text-blue-700">
                Dealers will be redirected to secure authentication. Managers use credentials provided by the dealer.
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/products.tsx.content:
import { useState } from "react";
import { useTranslation } from "react-i18next";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ArrowLeft, Plus, Edit, Trash2, Package } from "lucide-react";
import type { Product, InsertProduct } from "@shared/schema";

interface ProductsPageProps {
  onBack: () => void;
}

export default function ProductsPage({ onBack }: ProductsPageProps) {
  const { t } = useTranslation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);

  const [formData, setFormData] = useState<InsertProduct>({
    retailOutletId: "",
    name: "",
    pricePerLiter: "0",
  });

  const { data: products = [], isLoading } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: InsertProduct) => {
      const response = await apiRequest("/api/products", "POST", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: t("common.success"),
        description: "Product created successfully",
      });
      setIsDialogOpen(false);
      resetForm();
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
    },
    onError: (error) => {
      toast({
        title: t("common.error"),
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async (data: { id: string; product: Partial<InsertProduct> }) => {
      const response = await apiRequest(`/api/products/${data.id}`, "PUT", data.product);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: t("common.success"),
        description: "Product updated successfully",
      });
      setIsDialogOpen(false);
      resetForm();
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
    },
    onError: (error) => {
      toast({
        title: t("common.error"),
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest(`/api/products/${id}`, "DELETE");
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: t("common.success"),
        description: "Product deleted successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
    },
    onError: (error) => {
      toast({
        title: t("common.error"),
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      retailOutletId: "",
      name: "",
      pricePerLiter: "0",
    });
    setEditingProduct(null);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Validate required fields
    if (!formData.name.trim()) {
      toast({
        title: t("common.error"),
        description: "Product name is required",
        variant: "destructive",
      });
      return;
    }

    if (!formData.pricePerLiter || parseFloat(formData.pricePerLiter) <= 0) {
      toast({
        title: t("common.error"),
        description: "Valid price is required",
        variant: "destructive",
      });
      return;
    }

    if (editingProduct) {
      updateMutation.mutate({ id: editingProduct.id, product: formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  const handleEdit = (product: Product) => {
    setEditingProduct(product);
    setFormData({
      retailOutletId: product.retailOutletId,
      name: product.name,
      pricePerLiter: product.pricePerLiter,
    });
    setIsDialogOpen(true);
  };

  const handleDelete = (id: string, productName: string) => {
    if (window.confirm(`Are you sure you want to delete "${productName}"? This action cannot be undone.`)) {
      deleteMutation.mutate(id);
    }
  };

  const getFuelTypeLabel = (type: string) => {
    switch (type) {
      case "petrol":
        return t("tankManagement.petrol");
      case "diesel":
        return t("tankManagement.diesel");
      case "premium":
        return t("tankManagement.premium");
      default:
        return type;
    }
  };

  if (isLoading) {
    return (
      <div className="p-4">
        <div className="animate-pulse space-y-4">
          <div className="h-4 bg-gray-200 rounded w-3/4"></div>
          <div className="h-20 bg-gray-200 rounded"></div>
          <div className="h-4 bg-gray-200 rounded w-1/2"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4 pb-20">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center">
          <Button
            variant="ghost"
            size="icon"
            onClick={onBack}
            className="mr-3"
            data-testid="back-button"
          >
            <ArrowLeft className="h-5 w-5" />
          </Button>
          <h1 className="text-xl font-semibold" data-testid="page-title">
            Products
          </h1>
        </div>

        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={resetForm} data-testid="add-product-button">
              <Plus className="h-4 w-4 mr-2" />
              Add Product
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>
                {editingProduct ? "Edit Product" : "Add New Product"}
              </DialogTitle>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="name">Product Name</Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  placeholder="Enter product name"
                  required
                  data-testid="input-product-name"
                />
              </div>



              <div className="space-y-2">
                <Label htmlFor="pricePerLiter">Price per Liter (â‚¹)</Label>
                <Input
                  id="pricePerLiter"
                  type="number"
                  step="0.01"
                  value={formData.pricePerLiter}
                  onChange={(e) => setFormData({ ...formData, pricePerLiter: e.target.value })}
                  placeholder="0.00"
                  required
                  data-testid="input-price"
                />
              </div>

              <div className="flex space-x-3 pt-4">
                <Button
                  type="submit"
                  disabled={createMutation.isPending || updateMutation.isPending}
                  className="flex-1"
                  data-testid="save-product-button"
                >
                  {createMutation.isPending || updateMutation.isPending
                    ? t("common.saving")
                    : editingProduct
                    ? t("common.update")
                    : t("common.save")}
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsDialogOpen(false)}
                  className="flex-1"
                  data-testid="cancel-button"
                >
                  {t("common.cancel")}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Products List */}
      <div className="space-y-4">
        {products.length === 0 ? (
          <Card>
            <CardContent className="p-8 text-center">
              <Package className="h-12 w-12 mx-auto text-gray-400 mb-4" />
              <h3 className="text-lg font-semibold text-gray-600 mb-2">
                No Products Added
              </h3>
              <p className="text-gray-500 mb-4">
                Add your first product to start managing fuel inventory
              </p>
              <Button onClick={() => setIsDialogOpen(true)}>
                <Plus className="h-4 w-4 mr-2" />
                Add Product
              </Button>
            </CardContent>
          </Card>
        ) : (
          products.map((product: Product) => (
            <Card key={product.id} data-testid={`product-card-${product.id}`}>
              <CardHeader className="pb-2">
                <div className="flex items-center justify-between">
                  <CardTitle className="flex items-center text-lg">
                    <Package className="h-5 w-5 mr-2 text-primary" />
                    {product.name}
                  </CardTitle>
                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleEdit(product)}
                      data-testid={`edit-product-${product.id}`}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleDelete(product.id, product.name)}
                      data-testid={`delete-product-${product.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <div className="text-sm">
                  <div>
                    <p className="text-gray-600">Price per Liter</p>
                    <p className="font-medium text-lg">â‚¹{parseFloat(product.pricePerLiter).toFixed(2)}</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

