import { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ArrowLeft, MapPin, Phone, Mail, Building } from "lucide-react";

interface RODetailsPageProps {
  onBack: () => void;
}

export default function RODetailsPage({ onBack }: RODetailsPageProps) {
  const { t } = useTranslation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isEditing, setIsEditing] = useState(false);

  const { data: retailOutlet, isLoading } = useQuery({
    queryKey: ["/api/retail-outlet"],
  }) as { data: any, isLoading: boolean };

  const [formData, setFormData] = useState({
    name: "",
    address: "",
    phoneNumber: "",
    sapcode: "",
    oilCompany: "",
  });

  // Update form data when retail outlet data is loaded
  useEffect(() => {
    if (retailOutlet) {
      setFormData({
        name: retailOutlet?.name || "",
        address: retailOutlet?.address || "",
        phoneNumber: retailOutlet?.phoneNumber || "",
        sapcode: retailOutlet?.sapcode || "",
        oilCompany: retailOutlet?.oilCompany || "",
      });
    }
  }, [retailOutlet]);

  const updateMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest(`/api/retail-outlet/${retailOutlet.id}`, "PUT", data);
    },
    onSuccess: () => {
      toast({
        title: t("common.success"),
        description: t("roDetails.updateSuccess"),
      });
      setIsEditing(false);
      queryClient.invalidateQueries({ queryKey: ["/api/retail-outlet"] });
    },
    onError: (error) => {
      toast({
        title: t("common.error"),
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateMutation.mutate(formData);
  };

  const handleCancel = () => {
    if (retailOutlet) {
      setFormData({
        name: retailOutlet?.name || "",
        address: retailOutlet?.address || "",
        phoneNumber: retailOutlet?.phoneNumber || "",
        sapcode: retailOutlet?.sapcode || "",
        oilCompany: retailOutlet?.oilCompany || "",
      });
    }
    setIsEditing(false);
  };

  if (isLoading) {
    return (
      <div className="p-4">
        <div className="animate-pulse space-y-4">
          <div className="h-4 bg-gray-200 rounded w-3/4"></div>
          <div className="h-20 bg-gray-200 rounded"></div>
          <div className="h-4 bg-gray-200 rounded w-1/2"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4 pb-20">
      {/* Header */}
      <div className="flex items-center mb-6">
        <Button
          variant="ghost"
          size="icon"
          onClick={onBack}
          className="mr-3"
          data-testid="back-button"
        >
          <ArrowLeft className="h-5 w-5" />
        </Button>
        <h1 className="text-xl font-semibold" data-testid="page-title">
          {t("menu.roDetails")}
        </h1>
      </div>

      <Card>
        <CardHeader className="flex flex-row items-center justify-between">
          <CardTitle className="flex items-center">
            <Building className="h-5 w-5 mr-2 text-primary" />
            {t("roDetails.title")}
          </CardTitle>
          {!isEditing && (
            <Button
              variant="outline"
              size="sm"
              onClick={() => setIsEditing(true)}
              data-testid="edit-button"
            >
              {t("common.edit")}
            </Button>
          )}
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="name">{t("roDetails.name")}</Label>
              <Input
                id="name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                disabled={!isEditing}
                data-testid="input-name"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="address">{t("roDetails.address")}</Label>
              <Textarea
                id="address"
                value={formData.address}
                onChange={(e) => setFormData({ ...formData, address: e.target.value })}
                disabled={!isEditing}
                rows={3}
                data-testid="input-address"
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="phoneNumber">{t("setup.phoneNumber")}</Label>
                <div className="relative">
                  <Phone className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                  <Input
                    id="phoneNumber"
                    value={formData.phoneNumber}
                    onChange={(e) => setFormData({ ...formData, phoneNumber: e.target.value })}
                    disabled={!isEditing}
                    className="pl-10"
                    data-testid="input-contact"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="sapcode">{t("setup.sapcode")}</Label>
                <Input
                  id="sapcode"
                  value={formData.sapcode}
                  onChange={(e) => setFormData({ ...formData, sapcode: e.target.value })}
                  disabled={!isEditing}
                  data-testid="input-sapcode"
                />
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="oilCompany">{t("setup.oilCompany")}</Label>
              <Input
                id="oilCompany"
                value={formData.oilCompany}
                onChange={(e) => setFormData({ ...formData, oilCompany: e.target.value })}
                disabled={!isEditing}
                data-testid="input-oil-company"
              />
            </div>

            {isEditing && (
              <div className="flex space-x-3 pt-4">
                <Button
                  type="submit"
                  disabled={updateMutation.isPending}
                  className="flex-1"
                  data-testid="save-button"
                >
                  {updateMutation.isPending ? t("common.saving") : t("common.save")}
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  onClick={handleCancel}
                  className="flex-1"
                  data-testid="cancel-button"
                >
                  {t("common.cancel")}
                </Button>
              </div>
            )}
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/settings.tsx.content:
import { useState } from "react";
import { useTranslation } from "react-i18next";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { ArrowLeft, Settings, Languages, DollarSign, Shield, Fuel, LogOut } from "lucide-react";

interface SettingsPageProps {
  onBack: () => void;
}

export default function SettingsPage({ onBack }: SettingsPageProps) {
  const { t, i18n } = useTranslation();
  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();

  const [fuelPrices, setFuelPrices] = useState({
    petrolPrice: 0,
    dieselPrice: 0,
    cngPrice: 0,
  });

  const [appSettings, setAppSettings] = useState({
    enableNotifications: true,
    autoBackup: true,
    showLowStockAlerts: true,
    requireShiftConfirmation: true,
  });

  const { data: settings, isLoading } = useQuery({
    queryKey: ["/api/settings"],
    onSuccess: (data) => {
      if (data?.fuelPrices) {
        setFuelPrices(data.fuelPrices);
      }
      if (data?.appSettings) {
        setAppSettings(data.appSettings);
      }
    },
  });

  const updateSettingsMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("/api/settings", {
        method: "PATCH",
        body: data,
      });
    },
    onSuccess: () => {
      toast({
        title: t("common.success"),
        description: t("settings.updateSuccess"),
      });
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
    },
    onError: (error) => {
      toast({
        title: t("common.error"),
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleLanguageChange = (language: string) => {
    i18n.changeLanguage(language);
    localStorage.setItem('language', language);
    toast({
      title: t("common.success"),
      description: t("settings.languageChanged"),
    });
  };

  const handleFuelPriceUpdate = () => {
    updateSettingsMutation.mutate({
      fuelPrices,
    });
  };

  const handleAppSettingsUpdate = () => {
    updateSettingsMutation.mutate({
      appSettings,
    });
  };

  const handleLogout = () => {
    window.location.href = "/api/logout";
  };

  if (isLoading) {
    return (
      <div className="p-4">
        <div className="animate-pulse space-y-4">
          <div className="h-4 bg-gray-200 rounded w-3/4"></div>
          <div className="h-20 bg-gray-200 rounded"></div>
          <div className="h-20 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4 pb-20">
      {/* Header */}
      <div className="flex items-center mb-6">
        <Button
          variant="ghost"
          size="icon"
          onClick={onBack}
          className="mr-3"
          data-testid="back-button"
        >
          <ArrowLeft className="h-5 w-5" />
        </Button>
        <h1 className="text-xl font-semibold" data-testid="page-title">
          {t("menu.settings")}
        </h1>
      </div>

      <div className="space-y-6">
        {/* Language Settings */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Languages className="h-5 w-5 mr-2 text-primary" />
              {t("settings.language")}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-2">
              <Label>{t("settings.selectLanguage")}</Label>
              <Select value={i18n.language} onValueChange={handleLanguageChange}>
                <SelectTrigger data-testid="select-language">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="en">English</SelectItem>
                  <SelectItem value="hi">à¤¹à¤¿à¤‚à¤¦à¥€ (Hindi)</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </CardContent>
        </Card>

        {/* Fuel Prices */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <DollarSign className="h-5 w-5 mr-2 text-primary" />
              {t("settings.fuelPrices")}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="grid grid-cols-3 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="petrolPrice">{t("settings.petrolPrice")} (â‚¹/L)</Label>
                  <Input
                    id="petrolPrice"
                    type="number"
                    step="0.01"
                    value={fuelPrices.petrolPrice}
                    onChange={(e) => setFuelPrices({
                      ...fuelPrices,
                      petrolPrice: parseFloat(e.target.value) || 0
                    })}
                    data-testid="input-petrol-price"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="dieselPrice">{t("settings.dieselPrice")} (â‚¹/L)</Label>
                  <Input
                    id="dieselPrice"
                    type="number"
                    step="0.01"
                    value={fuelPrices.dieselPrice}
                    onChange={(e) => setFuelPrices({
                      ...fuelPrices,
                      dieselPrice: parseFloat(e.target.value) || 0
                    })}
                    data-testid="input-diesel-price"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="cngPrice">{t("settings.cngPrice")} (â‚¹/Kg)</Label>
                  <Input
                    id="cngPrice"
                    type="number"
                    step="0.01"
                    value={fuelPrices.cngPrice}
                    onChange={(e) => setFuelPrices({
                      ...fuelPrices,
                      cngPrice: parseFloat(e.target.value) || 0
                    })}
                    data-testid="input-cng-price"
                  />
                </div>
              </div>
              <Button
                onClick={handleFuelPriceUpdate}
                disabled={updateSettingsMutation.isPending}
                className="w-full"
                data-testid="update-fuel-prices-button"
              >
                {updateSettingsMutation.isPending ? t("common.saving") : t("settings.updatePrices")}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* App Settings */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Settings className="h-5 w-5 mr-2 text-primary" />
              {t("settings.appSettings")}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <div>
                  <Label htmlFor="enableNotifications">{t("settings.enableNotifications")}</Label>
                  <p className="text-sm text-gray-600">{t("settings.enableNotificationsDesc")}</p>
                </div>
                <Switch
                  id="enableNotifications"
                  checked={appSettings.enableNotifications}
                  onCheckedChange={(checked) => setAppSettings({
                    ...appSettings,
                    enableNotifications: checked
                  })}
                  data-testid="switch-notifications"
                />
              </div>

              <Separator />

              <div className="flex items-center justify-between">
                <div>
                  <Label htmlFor="autoBackup">{t("settings.autoBackup")}</Label>
                  <p className="text-sm text-gray-600">{t("settings.autoBackupDesc")}</p>
                </div>
                <Switch
                  id="autoBackup"
                  checked={appSettings.autoBackup}
                  onCheckedChange={(checked) => setAppSettings({
                    ...appSettings,
                    autoBackup: checked
                  })}
                  data-testid="switch-auto-backup"
                />
              </div>

              <Separator />

              <div className="flex items-center justify-between">
                <div>
                  <Label htmlFor="showLowStockAlerts">{t("settings.showLowStockAlerts")}</Label>
                  <p className="text-sm text-gray-600">{t("settings.showLowStockAlertsDesc")}</p>
                </div>
                <Switch
                  id="showLowStockAlerts"
                  checked={appSettings.showLowStockAlerts}
                  onCheckedChange={(checked) => setAppSettings({
                    ...appSettings,
                    showLowStockAlerts: checked
                  })}
                  data-testid="switch-low-stock-alerts"
                />
              </div>

              <Separator />

              <div className="flex items-center justify-between">
                <div>
                  <Label htmlFor="requireShiftConfirmation">{t("settings.requireShiftConfirmation")}</Label>
                  <p className="text-sm text-gray-600">{t("settings.requireShiftConfirmationDesc")}</p>
                </div>
                <Switch
                  id="requireShiftConfirmation"
                  checked={appSettings.requireShiftConfirmation}
                  onCheckedChange={(checked) => setAppSettings({
                    ...appSettings,
                    requireShiftConfirmation: checked
                  })}
                  data-testid="switch-shift-confirmation"
                />
              </div>

              <Button
                onClick={handleAppSettingsUpdate}
                disabled={updateSettingsMutation.isPending}
                className="w-full"
                data-testid="update-app-settings-button"
              >
                {updateSettingsMutation.isPending ? t("common.saving") : t("settings.updateSettings")}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Account Section */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Shield className="h-5 w-5 mr-2 text-primary" />
              {t("settings.account")}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                {user?.profileImageUrl ? (
                  <img 
                    src={user.profileImageUrl} 
                    alt="Profile" 
                    className="w-10 h-10 rounded-full object-cover"
                  />
                ) : (
                  <div className="w-10 h-10 bg-primary/20 rounded-full flex items-center justify-center">
                    <span className="text-sm font-medium text-primary">
                      {(user?.firstName || user?.email || "U").charAt(0).toUpperCase()}
                    </span>
                  </div>
                )}
                <div>
                  <p className="font-medium">{user?.firstName || user?.email}</p>
                  <p className="text-sm text-gray-600">{user?.email}</p>
                </div>
              </div>

              <Separator />

              <Button
                variant="destructive"
                onClick={handleLogout}
                className="w-full"
                data-testid="logout-button"
              >
                <LogOut className="h-4 w-4 mr-2" />
                {t("settings.logout")}
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/tank-management.tsx.content:
import { useState } from "react";
import { useTranslation } from "react-i18next";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { ArrowLeft, Plus, Truck, Edit3, Trash2, Fuel } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";

interface TankManagementPageProps {
  onBack: () => void;
}

const tankSchema = z.object({
  tankNumber: z.string().min(1, "Tank number is required"),
  productId: z.string().min(1, "Product is required"),
  capacity: z.number().min(1, "Capacity must be greater than 0"),
  length: z.number().min(0.1, "Length must be greater than 0"),
  diameter: z.number().min(0.1, "Diameter must be greater than 0"),
});

type TankForm = z.infer<typeof tankSchema>;

export default function TankManagementPage({ onBack }: TankManagementPageProps) {
  const { t } = useTranslation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingTank, setEditingTank] = useState<any>(null);

  const { data: tanks = [], isLoading } = useQuery({
    queryKey: ["/api/tanks"],
  }) as { data: any[], isLoading: boolean };

  const { data: products = [] } = useQuery({
    queryKey: ["/api/products"],
  }) as { data: any[] };

  const form = useForm<TankForm>({
    resolver: zodResolver(tankSchema),
    defaultValues: {
      tankNumber: "",
      productId: "",
      capacity: 0,
      length: 0,
      diameter: 0,
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: TankForm) => {
      const response = await apiRequest("/api/tanks", "POST", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: t("common.success"),
        description: t("tankManagement.createSuccess"),
      });
      setIsDialogOpen(false);
      form.reset();
      queryClient.invalidateQueries({ queryKey: ["/api/tanks"] });
    },
    onError: (error) => {
      toast({
        title: t("common.error"),
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: TankForm }) => {
      const response = await apiRequest(`/api/tanks/${id}`, "PUT", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: t("common.success"),
        description: t("tankManagement.updateSuccess"),
      });
      setIsDialogOpen(false);
      setEditingTank(null);
      form.reset();
      queryClient.invalidateQueries({ queryKey: ["/api/tanks"] });
    },
    onError: (error) => {
      toast({
        title: t("common.error"),
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest(`/api/tanks/${id}`, "DELETE");
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: t("common.success"),
        description: t("tankManagement.deleteSuccess"),
      });
      queryClient.invalidateQueries({ queryKey: ["/api/tanks"] });
    },
    onError: (error) => {
      toast({
        title: t("common.error"),
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (data: TankForm) => {
    if (editingTank) {
      updateMutation.mutate({ id: editingTank.id, data });
    } else {
      createMutation.mutate(data);
    }
  };

  const handleEdit = (tank: any) => {
    setEditingTank(tank);
    form.reset({
      tankNumber: tank.tankNumber,
      productId: tank.productId,
      capacity: tank.capacity,
      length: tank.length,
      diameter: tank.diameter,
    });
    setIsDialogOpen(true);
  };

  const handleAddNew = () => {
    setEditingTank(null);
    form.reset();
    setIsDialogOpen(true);
  };

  const getFuelTypeColor = (fuelType: string) => {
    switch (fuelType) {
      case "petrol": return "text-red-600 bg-red-50";
      case "diesel": return "text-yellow-600 bg-yellow-50";
      case "cng": return "text-green-600 bg-green-50";
      default: return "text-gray-600 bg-gray-50";
    }
  };

  const getStatusColor = (currentLevel: number, minThreshold: number, capacity: number) => {
    const percentage = (currentLevel / capacity) * 100;
    if (currentLevel <= minThreshold) return "text-red-600 bg-red-50";
    if (percentage < 25) return "text-orange-600 bg-orange-50";
    return "text-green-600 bg-green-50";
  };

  if (isLoading) {
    return (
      <div className="p-4">
        <div className="animate-pulse space-y-4">
          <div className="h-4 bg-gray-200 rounded w-3/4"></div>
          <div className="h-20 bg-gray-200 rounded"></div>
          <div className="h-20 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4 pb-20">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center">
          <Button
            variant="ghost"
            size="icon"
            onClick={onBack}
            className="mr-3"
            data-testid="back-button"
          >
            <ArrowLeft className="h-5 w-5" />
          </Button>
          <h1 className="text-xl font-semibold" data-testid="page-title">
            {t("menu.tankManagement")}
          </h1>
        </div>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={handleAddNew} data-testid="add-tank-button">
              <Plus className="h-4 w-4 mr-2" />
              {t("tankManagement.addTank")}
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>
                {editingTank ? t("tankManagement.editTank") : t("tankManagement.addTank")}
              </DialogTitle>
              <DialogDescription>
                {editingTank ? t("tankManagement.editDescription") : t("tankManagement.addDescription")}
              </DialogDescription>
            </DialogHeader>
            <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="tankNumber">Tank Number</Label>
                <Input
                  id="tankNumber"
                  {...form.register("tankNumber")}
                  data-testid="input-tank-number"
                  placeholder="Tank 1, Tank 2, etc."
                />
                {form.formState.errors.tankNumber && (
                  <p className="text-sm text-red-600">{form.formState.errors.tankNumber.message}</p>
                )}
              </div>

              <div className="space-y-2">
                <Label>Product</Label>
                <Select
                  value={form.watch("productId")}
                  onValueChange={(value) => form.setValue("productId", value)}
                >
                  <SelectTrigger data-testid="select-product">
                    <SelectValue placeholder="Select a product" />
                  </SelectTrigger>
                  <SelectContent>
                    {products.map((product: any) => (
                      <SelectItem key={product.id} value={product.id}>
                        {product.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                {form.formState.errors.productId && (
                  <p className="text-sm text-red-600">{form.formState.errors.productId.message}</p>
                )}
              </div>

              <div className="space-y-2">
                <Label htmlFor="capacity">Capacity (L)</Label>
                <Input
                  id="capacity"
                  type="number"
                  {...form.register("capacity", { valueAsNumber: true })}
                  data-testid="input-capacity"
                />
                {form.formState.errors.capacity && (
                  <p className="text-sm text-red-600">{form.formState.errors.capacity.message}</p>
                )}
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="length">Length (m)</Label>
                  <Input
                    id="length"
                    type="number"
                    step="0.01"
                    placeholder="e.g. 1.5"
                    {...form.register("length", { valueAsNumber: true })}
                    data-testid="input-length"
                  />
                  {form.formState.errors.length && (
                    <p className="text-sm text-red-600">{form.formState.errors.length.message}</p>
                  )}
                </div>
                <div className="space-y-2">
                  <Label htmlFor="diameter">Diameter (m)</Label>
                  <Input
                    id="diameter"
                    type="number"
                    step="0.01"
                    placeholder="e.g. 2.5"
                    {...form.register("diameter", { valueAsNumber: true })}
                    data-testid="input-diameter"
                  />
                  {form.formState.errors.diameter && (
                    <p className="text-sm text-red-600">{form.formState.errors.diameter.message}</p>
                  )}
                </div>
              </div>

              <div className="flex space-x-3 pt-4">
                <Button
                  type="submit"
                  disabled={createMutation.isPending || updateMutation.isPending}
                  className="flex-1"
                  data-testid="save-tank-button"
                >
                  {(createMutation.isPending || updateMutation.isPending) ? 
                    t("common.saving") : t("common.save")}
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsDialogOpen(false)}
                  className="flex-1"
                  data-testid="cancel-tank-button"
                >
                  {t("common.cancel")}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Tanks List */}
      {tanks.length === 0 ? (
        <Card className="text-center py-8">
          <CardContent>
            <Truck className="h-12 w-12 mx-auto text-gray-400 mb-4" />
            <h3 className="text-lg font-medium mb-2">{t("tankManagement.noTanks")}</h3>
            <p className="text-gray-600 mb-4">{t("tankManagement.noTanksDescription")}</p>
            <Button onClick={handleAddNew} data-testid="add-first-tank-button">
              <Plus className="h-4 w-4 mr-2" />
              {t("tankManagement.addFirstTank")}
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-4">
          {tanks.map((tank: any) => {
            const product = products.find((p: any) => p.id === tank.productId);
            return (
              <Card key={tank.id} data-testid={`tank-card-${tank.id}`}>
                <CardContent className="p-4">
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center space-x-3">
                      <div className="p-2 rounded-lg bg-primary/10">
                        <Fuel className="h-5 w-5 text-primary" />
                      </div>
                      <div>
                        <h3 className="font-medium" data-testid={`tank-name-${tank.id}`}>
                          Tank {tank.tankNumber}
                        </h3>
                        <span className="inline-block px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                          {product?.name || 'Unknown Product'}
                        </span>
                      </div>
                    </div>
                    <div className="flex space-x-2">
                      <Button
                        variant="outline"
                        size="icon"
                        onClick={() => handleEdit(tank)}
                        data-testid={`edit-tank-${tank.id}`}
                      >
                        <Edit3 className="h-4 w-4" />
                      </Button>
                      <Button
                        variant="outline"
                        size="icon"
                        onClick={() => deleteMutation.mutate(tank.id)}
                        disabled={deleteMutation.isPending}
                        data-testid={`delete-tank-${tank.id}`}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>

                  <div className="space-y-3">
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <span className="text-gray-600">Capacity:</span>
                        <span className="ml-2 font-medium">{tank.capacity}L</span>
                      </div>
                      <div>
                        <span className="text-gray-600">Length:</span>
                        <span className="ml-2 font-medium">{tank.length}m</span>
                      </div>
                      <div>
                        <span className="text-gray-600">Diameter:</span>
                        <span className="ml-2 font-medium">{tank.diameter}m</span>
                      </div>
                      <div>
                        <span className="text-gray-600">Price:</span>
                        <span className="ml-2 font-medium">â‚¹{product?.pricePerLiter || 0}/L</span>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>
      )}
    </div>
  );
}
